import React, { useState } from 'react';
import Stepper from "@material-ui/core/Stepper";
import { StepLabel ,Step } from '@material-ui/core';
import Button from "@material-ui/core/Button";
const Register =() =>  {
  const [activeStep,setActiveStep]=useState(0);
//   1 step is completed
const nextStep = () =>{
                      if(activeStep<5)
       setActiveStep((activeStep => activeStep+1))
}
const previousStep = () =>{
                      if(activeStep !=-1)//activeStep=-1 means no step is completed
                      setActiveStep((activeStep => activeStep-1))
               }
  return(   <div><Stepper activeStep={activeStep}>{/* orientation=vertical */}
                      <Step >
                                   <StepLabel>First</StepLabel>
                      </Step> 
                      <Step>
                                   <StepLabel>Second</StepLabel>
                      </Step> 
                      <Step>
                                   <StepLabel>Third</StepLabel>
                      </Step>  
                      <Step>
                                   <StepLabel>Fourth</StepLabel>
                      </Step>     
                      <Step>
                                   <StepLabel>Fifth</StepLabel>
                      </Step>        
                 </Stepper>
                 <Button variant='outlined' color='primary'  onClick={() => nextStep()}>{activeStep===4?Finish:Next} </Button>
                 <br/>
                 <br/>
                 <Button variant='outlined' color='primary' disabled = {activeStep === 0} onClick={() => previousStep()}>Go to previous step</Button>
                 </div>   
                 
                 );
}
export default Register;
