import React, { useState } from 'react';
import Stepper from "@material-ui/core/Stepper";
import { StepLabel } from '@material-ui/core';
import Button from "@material-ui/core/Button";
function Register()  {
  const [activeStep,setActiveStep]=useState(1);
//   1 step is completed
const nextStep = () =>{
                      if(activeStep<4)
       setActiveStep((activeStep => activeStep+1))
}
const previousStep = () =>{
                      if(activeStep !=-1)
                      setActiveStep((activeStep => activeStep-1))
               }
  return(   <div><Stepper>
                      <Step activeStep={activeStep}>
                                   <StepLabel>First</StepLabel>
                      </Step> 
                      <Step>
                                   <StepLabel>Second</StepLabel>
                      </Step> 
                      <Step>
                                   <StepLabel>Third</StepLabel>
                      </Step>  
                      <Step>
                                   <StepLabel>Fourth</StepLabel>
                      </Step>     
                      <Step>
                                   <StepLabel>Fifth</StepLabel>
                      </Step>        
                 </Stepper>
                 <Button variant='outlined' color='primary' onClick={() => nextStep()}>Completed step </Button>
                 <br/>
                 <br/>
                 <Button variant='outlined' color='primary' onClick={() => previousStep()}>Go to previous step</Button>
                 </div>   
                 
                 );
}
export default Register;
